#Proj01rods_str = input("Input rods:")rods_float = float(rods_str)meters = rods_float*5.0292 feet = meters/0.3048miles = meters/1609.34furlongs = rods_float/40walking_min = (miles/3.1)*60print("You input", rods_float, "rods.")print("Meters:", round(meters, 3))print("Feet:", round(feet,3))print("Miles:", round(miles, 3))print("Furlongs:", round(furlongs, 3))print("Minutes to walk", rods_float, "rods:", round(walking_min, 3))